<?phpif (session_id() == "") session_start(); // if no active session we start a new onerequire("config.php");$user = $_POST['username'];$pass = $_POST['password'];$link = mysql_connect($db_host, $db_username, $db_password)   or die('Could not connect: ' . mysql_error());mysql_select_db($db_name) or die('Could not select database');$query = "SELECT * FROM ${db_prefix}users WHERE username='${user}' LIMIT 1";$result = mysql_query($query) or die('Query failed: ' . mysql_error());$line = mysql_fetch_array($result, MYSQL_ASSOC);if($line){	if($_POST['encrypted'] == "false") { $pass = crypt($pass, $conf_secretword); }	if($line["password"] == $pass)	{		$_SESSION['userid'] = $line['ID'];		$_SESSION['username'] = $line['username'];		$_SESSION['userloggedin'] = TRUE;		if($_POST['remember'] == "true" && $_POST['encrypted'] == "false")		{			setcookie("psychdesktop_remember", $user.",".$pass, time()+60*60*24*30, "/");		}		echo "0";	}}else{	echo "1";}mysql_free_result($result);mysql_close($link);?>