nwinHTML += \\\"<a onclick=\\\'document.getElementById(\\\\\\\"ctrlpanel_color\\\\\\\").value = \\\\\\\"purple\\\\\\\";\\\' style=\\\'background: purple; float:left; width:20px; height:20px; margin:4px; cursor:pointer; outline:none;\\\'></a>\\\";\r\nwinHTML += \\\"<a onclick=\\\'document.getElementById(\\\\\\\"ctrlpanel_color\\\\\\\").value = \\\\\\\"black\\\\\\\";\\\' style=\\\'background: black; float:left; width:20px; height:20px; margin:4px; cursor:pointer; outline:none;\\\'></a>\\\";\r\nwinHTML += \\\"<a onclick=\\\'document.getElementById(\\\\\\\"ctrlpanel_color\\\\\\\").value = \\\\\\\"white\\\\\\\";\\\' style=\\\'background: white; float:left; width:20px; height:20px; margin:4px; cursor:pointer; outline:none;\\\'></a>\\\";\r\nwinHTML += \\\"</div><br /><br /><b>Or HTML color:</b><input type=\\\'text\\\' id=\\\'ctrlpanel_color\\\' maxlength=\\\'7\\\' size=\\\'7\\\' value=\\\'#\\\' />\\\";\r\nwinHTML += \\\"</fieldset>\\\";\r\nwinHTML += \\\"<fieldset>\\\"\r\nwinHTML += \\\"<legend>Background Image</legend>\\\";\r\nwinHTML += \\\"<b>Default: </b><input checked type=\\\'radio\\\' name=\\\'ctrlpanel_image\\\' onFocus=\\\'document.getElementById(\\\\\\\"ctrlpanel_text\\\\\\\").value = \\\\\\\"./wallpaper/default.gif\\\\\\\";\\\' />\\\";\r\nwinHTML += \\\"<b>None: </b><input type=\\\'radio\\\' name=\\\'ctrlpanel_image\\\' onFocus=\\\'document.getElementById(\\\\\\\"ctrlpanel_text\\\\\\\").value = \\\\\\\"\\\\\\\";\\\' />\\\";\r\nwinHTML += \\\"<b>Other: </b><input type=\\\'radio\\\' name=\\\'ctrlpanel_image\\\' id=\\\'ctrlpanel_image_other\\\' onFocus=\\\'document.getElementById(\\\\\\\"ctrlpanel_text\\\\\\\").value = \\\\\\\"http://\\\\\\\";\\\' />\\\";\r\nwinHTML += \\\"<br /><b>Image URL: </b><input type=\\\'text\\\' name=\\\'ctrlpanel_text\\\' value=\\\'./wallpaper/default.gif\\\' id=\\\'ctrlpanel_text\\\' />\\\";\r\nwinHTML += \\\"</fieldset><br /><input type=\\\'button\\\' onClick=\\\'api.registry.saveValue(0, \\\\\\\"bgimg\\\\\\\", document.getElementById(\\\\\\\"ctrlpanel_text\\\\\\\").value); desktop.wallpaper.set(document.getElementById(\\\\\\\"ctrlpanel_text\\\\\\\").value); desktop.wallpaper.setColor(document.getElementById(\\\\\\\"ctrlpanel_color\\\\\\\").value); api.registry.saveValue(0, \\\\\\\"bgcolor\\\\\\\", document.getElementById(\\\\\\\"ctrlpanel_color\\\\\\\").value);\\\' value=\\\'Save\\\' />\\\";\r\nwinHTML += \\\"</div>\\\";\r\nthis.window = new api.window();\r\nthis.window.title=\\\"Control Panel\\\";\r\nthis.window.width=\\\"330px\\\";\r\nthis.window.height=\\\"275px\\\";\r\nthis.window.write(winHTML);\r\nthis.window.show();\r\napi.registry.getValue(0, \\\"bgimg\\\", this.hitch(this.loadvalue_img));\r\napi.registry.getValue(0, \\\"bgcolor\\\", this.hitch(this.loadvalue_color));\r\n}\r\n\r\nthis.loadvalue_img = function(img) {\r\ndocument.getElementById(\\\"ctrlpanel_text\\\").value = img;\r\n}\r\nthis.loadvalue_color = function(color) {\r\ndocument.getElementById(\\\"ctrlpanel_color\\\").value = color;\r\n}','1.0','Stable','System'),
(4, 'Notepad', 'Psychiccyberfreak', 'bj@psychdesigns.net', 'this.init=function(args)\r\n{\r\nthis.window2 = new api.window();\r\nthis.window2.write(\\"Please wait whilst we get data from the registry...\\");\r\nthis.window2.height=\\"100px\\";\r\nthis.window2.width=\\"100px\\";\r\nthis.window2.title=\\"Please wait...\\";\r\nthis.window2.show();\r\napi.registry.get(this.id, \\"notepad\\", dojo.hitch(this, this.notepad));\r\n}\r\n\r\nthis.notepad=function(text) {\r\nthis.window2.destroy();\r\nthis.window = new api.window();\r\n\r\nthis.window.write(\\"<div padding=10>\\");\r\nthis.window.write(\\"<textarea id=\\''notepad\\"+this.instance+\\"\\'' style=\\''width: 100%; height: 90%;\\''>\\");\r\nthis.window.write(text);\r\nthis.window.write(\\"</textarea>\\");\r\nthis.window.write(\\"<input onclick=\\\\\\"note = document.getElementById(\\''notepad\\"+this.instance+\\"\\'').value.replace(/\\\\\\\\n/gi, \\'' \\'' ); api.registry.save(\\"+this.id+\\", \\''notepad\\'', note);\\\\\\" value=\\''Save\\'' type=\\''button\\'' />\\");\r\n\r\nthis.window.title=\\"Notepad\\";\r\nthis.window.width=\\"300px\\";\r\nthis.window.height=\\"305px\\";\r\nthis.window.show();\r\n}', '1.0', 'Beta', 'Accessories'),
(5,'File Get Test','Jaymacdonald','jaymac407@gmail.com','this.init = function(args)\r\n{\r\napi.fs.getFile(\\\"docs/ReadMe.txt\\\",this.hitch(this.start));\r\n}\r\n\r\nthis.start=function(){\r\nwinHTML = \\\"Path: \\\" + api.fs.getFileResult[\\\"path\\\"] + \\\"<br>\\\";\r\nwinHTML += \\\"Contents: \\\"+api.fs.getFileResult[\\\"contents\\\"];\r\nthis.window = new api.window();\r\nthis.window.write(winHTML);\r\nthis.window.height=\\\"305px\\\";\r\nthis.window.width=\\\"300px\\\";\r\nthis.window.title=\\\"Notepad\\\";\r\nthis.window.show();\r\n}','1.0','Beta','System'),
(6,'File Explorer','Jaymacdonald','jaymac407@gmail.com','this.init = function(args)\r\n{\r\napi.fs.listFiles(this.hitch(this.start));\r\n}\r\n\r\nthis.start = function() {\r\n  var innerHTML;\r\n  innerHTML = \\\"<h2>File Explorer</h2>\\\";\r\n  var i = 0;\r\n  jayCount = api.fs.listFilesResult[\\\"count\\\"];\r\n  while(i <= jayCount)\r\n  {\r\n    innerHTML += \\\"<p> Path: \\\"+api.fs.listFilesResult[i][\\\"path\\\"]+\\\"</p>\\\";\r\n    i++;\r\n  }\r\n  innerHTML += \\\"End of file list.\\\";\r\n  this.win1 = new api.window();\r\n  this.win1.write(innerHTML);\r\n  this.win1.title = \\\"File Explorer\\\";\r\n  this.win1.height = \\\"190px\\\";\r\n  this.win1.width = \\\"200px\\\";\r\n  this.win1.show();\r\n}','0.1','Beta','Office'),
(7,'Katana IDE','Psychiccyberfreak','bj@psychdesigns.net','/*\r\nKatana IDE\r\nby: Psychiccyberfreak\r\n\r\nTODO:\r\n*restrict save/open backend to admins and developers\r\n*get function parser working\r\n*custom code snipplets (use database API)\r\n*layoutContainer resize issue (nobody has been able to answer this in the dojo forums...)\r\n\r\nThoughts after writing an app:\r\n*all of the system functions and variables should be in a class. It will be a pain in the ass to do, but otherwise it would be easy to screw up the namespace in an app.\r\napps[blah] should be a cache of the apps, instances[blah] should hold each instance.\r\n*/\r\nthis.init = function(args)\r\n{\r\nthis.metadata = new Object();\r\nthis.functions = new Array();\r\nthis.window = new api.window();\r\nthis.window.title = \\\"Katana IDE\\\";\r\nthis.window.write(\\\"<div id=\\\'ide\\\"+this.instance+\\\"\\\' style=\\\'height: 100%; width: 100%; margin: 0px;\\\'></div>\\\");\r\nthis.window.show();\r\n\r\nthis.ide = dojo.widget.createWidget(\\\"LayoutContainer\\\", {style: \\\"width: 100%; height: 100%;\\\", layoutAlign: \\\"client\\\"}, dojo.byId(\\\"ide\\\"+this.instance));\r\n     this.toolbarcontainer = dojo.widget.createWidget(\\\"ContentPane\\\",{layoutAlign: \\\"top\\\", style: \\\"width: 100%;\\\"});\r\n     this.toolbar = dojo.widget.createWidget(\\\"MenuBar2\\\", {layout: \\\"top\\\"});\r\n\r\n          this.toolbar_item = dojo.widget.createWidget(\\\"PopupMenu2\\\", {id: \\\"ide_file\\\"+this.instance});\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"New\\\", onClick: this.hitch(this.file_new)}));\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Open\\\", onClick: this.hitch(this.loadDialog)}));\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Save\\\", onClick: this.hitch(this.save)}));\r\n          this.toolbar.addChild(dojo.widget.createWidget(\\\"MenuBarItem2\\\", {caption: \\\"File\\\", submenuId: \\\"ide_file\\\"+this.instance}));\r\n\r\n          this.toolbar_item = dojo.widget.createWidget(\\\"PopupMenu2\\\", {id: \\\"ide_insert\\\"+this.instance});\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Ajax request\\\", onClick: this.hitch(this.insert_ajax)}));\r\n          this.toolbar.addChild(dojo.widget.createWidget(\\\"MenuBarItem2\\\", {caption: \\\"Insert\\\", submenuId: \\\"ide_insert\\\"+this.instance}));\r\n\r\n          this.toolbar_item = dojo.widget.createWidget(\\\"PopupMenu2\\\", {id: \\\"ide_functions\\\"+this.instance});\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Add new\\\", onClick: this.hitch(this.addafunc)}));\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Edit Current\\\", onClick: this.hitch(this.functionDialog)}));\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Delete Current\\\", onClick: this.hitch(this.deleteFunc)}));\r\n          this.toolbar.addChild(dojo.widget.createWidget(\\\"MenuBarItem2\\\", {caption: \\\"Functions\\\", submenuId: \\\"ide_functions\\\"+this.instance}));\r\n\r\n          this.toolbar_item = dojo.widget.createWidget(\\\"PopupMenu2\\\", {id: \\\"ide_app\\\"+this.instance});\r\n               this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Flush app cache\\\", onClick: function(){apps = new Array();}}));\r\n          this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Execute App\\\", onClick: this.hitch(this.execute)}));\r\n          this.toolbar_item.addChild(dojo.widget.createWidget(\\\"MenuItem2\\\", {caption: \\\"Edit Metadata\\\", onClick: this.hitch(this.showMetadataDialog) }));\r\n\r\n          this.toolbar.addChild(dojo.widget.createWidget