     if(funcNumber == -1)\r\n          {\r\n               this.addFuncDialog.write(\\\"<b>Name:</b> <input type=\\\'text\\\' id=\\\'ide_funcDialog_name\\\"+this.instance+\\\"\\\' /><br />\\\");\r\n               this.addFuncDialog.write(\\\"<b>Arguments:</b> <input type=\\\'text\\\' id=\\\'ide_funcDialog_args\\\"+this.instance+\\\"\\\' /><br />\\\");\r\n               this.addFuncDialog.write(\\\"<input type=\\\'button\\\' value=\\\'Save\\\' id=\\\'ide_funcDialog_saveButton\\\"+this.instance+\\\"\\\' onClick=\\\'desktop.app.instances[\\\"+this.instance+\\\"].createFunction();\\\' />\\\");\r\n               this.addFuncDialog.show();\r\n          }\r\n          else\r\n          {\r\n               funcNumber = this.currentFunc;\r\n               //populate form with function info (this.functions[funcNumber].name, this.functions[funcNumber].args),\r\n               this.addFuncDialog.write(\\\"<b>Name:</b> <input type=\\\'text\\\' id=\\\'ide_funcDialog_name\\\"+this.instance+\\\"\\\' value=\\\'\\\"+this.functions[funcNumber].name+\\\"\\\' /><br />\\\");\r\n               this.addFuncDialog.write(\\\"<b>Arguments:</b> <input type=\\\'text\\\' id=\\\'ide_funcDialog_args\\\"+this.instance+\\\"\\\' value=\\\'\\\"+this.functions[funcNumber].args+\\\"\\\' /><br />\\\");\r\n               this.addFuncDialog.write(\\\"<input type=\\\'button\\\' value=\\\'Save\\\' id=\\\'ide_funcDialog_saveButton\\\"+this.instance+\\\"\\\' onClick=\\\'desktop.app.instances[\\\"+this.instance+\\\"].editFunction();\\\' />\\\");\r\n               this.addFuncDialog.write(\\\"<input type=\\\'hidden\\\' id=\\\'ide_funcDialog_funcNumber\\\"+this.instance+\\\"\\\' value=\\\'\\\"+funcNumber+\\\"\\\' />\\\");\r\n               this.addFuncDialog.show();\r\n          }\r\n     }\r\n}\r\nthis.createFunction = function()\r\n{\r\n     if(document.getElementById(\\\"ide_funcDialog_name\\\"+this.instance).value != \\\"\\\")\r\n     {\r\n          name = document.getElementById(\\\"ide_funcDialog_name\\\"+t